var appName = "RDPCustomPort";
var app = Application (appName);
var RDPRecord_client = "";
var RDPRecord_server = "";
var RDPRecord_port = "";
var RDPRecord_cport = "";
var RDPDetail = "";

if ( event == "RDP_TICK" && Flow.server.port !=3389) {
    debug ('client : ' + Flow.client.ipaddr);
    debug ('client port : ' + Flow.client.port);
    debug ('server : ' + Flow.server.ipaddr);
    debug ('server port: ' + Flow.server.port);

    RDPRecord_client = "" + Flow.client.ipaddr;
    RDPRecord_server = "" + Flow.server.ipaddr;
    RDPRecord_port = "" + Flow.server.port;
    RDPRecord_cport = '' + Flow.client.port;
    RDPDetail = 'Client ' + Flow.client.ipaddr + ' connect to ' + Flow.server.ipaddr + ' using port ' + Flow.server.port;
    //Base Metric
    app.metricAddCount("RDP_Custom_Cnt", 1); 

    //Detail metrics of RDP_Custom_Cnt
    app.metricAddDetailCount("RDPRecord_client" , RDPRecord_client, 1);
    app.metricAddDetailCount("RDPRecord_server" , RDPRecord_server, 1);
    app.metricAddDetailCount("RDPRecord_port" , RDPRecord_port, 1);
    app.metricAddDetailCount("RDPRecord_cport", RDPRecord_cport, 1);
    app.metricAddDetailCount("RDPDetail", RDPDetail, 1);

    /*
    commitRecord("RDPClientPort", RDPRecord_cport.toString());
